#!/usr/bin/env bash
set -euxo pipefail
sudo useradd -m ansible -u 1042 -s /bin/bash
sudo bash -c "echo 'ansible ALL=(ALL) NOPASSWD:ALL' > /etc/sudoers.d/ansible"
sudo chmod 440 /etc/sudoers.d/ansible
sudo mkdir -p mkdir /home/ansible/.ssh
sudo chown -R ansible:ansible /home/ansible
sudo chmod 700 /home/ansible/.ssh
sudo -u ansible bash -c "echo 'ssh-rsa  galaxy' > /home/ansible/.ssh/authorized_keys"
sudo -u ansible bash -c "echo 'ssh-rsa  cygnus' >> /home/ansible/.ssh/authorized_keys"
sudo -u ansible bash -c "echo 'ssh-rsa  narvi' >> /home/ansible/.ssh/authorized_keys"
sudo touch /var/log/ansible.log
sudo chown ansible:ansible /var/log/ansible.log
